mapper.select:
select BOARD_ID, TOPIC_ID, USER_ID, UPVOTE, DOWNVOTE, TITLE, CONTENT, to_char(POST_TIME,'yyyy-mm-dd hh24:mi'),VIEWS 
	from (select t1.*, rownum rn  
		from (SELECT BOARD_ID, TOPIC_ID, USER_ID, UPVOTE, DOWNVOTE, TITLE, CONTENT, POST_TIME,VIEWS FROM BOARD order by board_id desc) t1 )
where rn between #{startRownum} and #{endRownum}
